package com.scarlatti.swingutils.filechooser;

import com.scarlatti.swingutils.SwingUtils;
import com.scarlatti.swingutils.Widget;

import javax.swing.*;
import javax.swing.event.DocumentEvent;
import javax.swing.event.DocumentListener;
import java.awt.*;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.function.Consumer;
import java.util.function.Supplier;

import static com.scarlatti.swingutils.SwingUtils.IconScale.ICON_SCALE_15;

/**
 * @author Alessandro Scarlatti
 * @since Tuesday, 12/25/2018
 */
public class FileChooserWidget implements Widget {

    private FileChooserWidgetProps props = new FileChooserWidgetProps();
    private FileChooserWidgetState state = new FileChooserWidgetState();
    private JPanel widgetPanel;
    private JTextField fileTextField;
    private JButton fileChooserButton;

    public FileChooserWidget() {
    }

    public FileChooserWidget(FileChooserWidgetProps props) {
        this.props = props;
    }

    public FileChooserWidget(Consumer<FileChooserWidgetProps> config) {
        props.self = this;
        config.accept(props);
    }

    public static Container ui(Consumer<FileChooserWidgetProps> config) {
        return new FileChooserWidget(config).getUi();
    }

    @Override
    public Container getUi() {
        if (widgetPanel == null) {
            initUi();
            initEvents();
        }

        return widgetPanel;
    }

    Container initUi() {
        widgetPanel = new JPanel();
        GroupLayout gl = new GroupLayout(widgetPanel);
        widgetPanel.setLayout(gl);

        // build file text field
        fileTextField = new JTextField();
        if (props.initialFile != null)
            fileTextField.setText(props.initialFile.toAbsolutePath().toString());

        // build file chooser button
        fileChooserButton = new JButton();
        Icon popoutIcon = SwingUtils.createScaledIcon(getPopoutIconBase64String(), ICON_SCALE_15);
        fileChooserButton.setIcon(popoutIcon);

        // configure layout
        gl.setAutoCreateGaps(true);
        gl.setAutoCreateContainerGaps(true);

        GroupLayout.SequentialGroup widgetHGroup = gl
            .createSequentialGroup()
            .addComponent(fileTextField)
            .addComponent(fileChooserButton);

        GroupLayout.ParallelGroup widgetVGroup = gl
            .createParallelGroup(GroupLayout.Alignment.CENTER, false)
            .addComponent(fileTextField)
            .addComponent(fileChooserButton);

        gl.setHorizontalGroup(widgetHGroup);
        gl.setVerticalGroup(widgetVGroup);

        return widgetPanel;
    }

    private String getPopoutIconBase64String() {
        return "";
    }

    private void initEvents() {
        // when the text changes fire pathChanged event
        fileTextField.getDocument().addDocumentListener(new DocumentListener() {
            public void changedUpdate(DocumentEvent e) {
                pathChanged();
            }

            public void removeUpdate(DocumentEvent e) {
                pathChanged();
            }

            public void insertUpdate(DocumentEvent e) {
                pathChanged();
            }
        });

        // when the button is clicked open a file chooser dialog
        // with the path to the text
        fileChooserButton.addActionListener(e -> {

            // we can invert control here, and delegate to a prop
            // that would be the choose file strategy.
            // this is a hard dependency now on the Windows File Chooser...

            if (props.fileChoiceStrategy == null)
                return;

            Path selectedFile = props.fileChoiceStrategy.get();

            // this is where we would probably rebuild the ui...
            if (selectedFile != null) {
                if (Files.exists(selectedFile)) {
                    state.path = selectedFile;
                    fileTextField.setText(selectedFile.toString());
                } else {
                    throw new RuntimeException("File does not exist " + selectedFile);
                }
            }

//            new WindowsFileChooser()
//                .withInitialFile(state.path)
//                .withGuiParent(widgetPanel)
//                .getFile();
        });
    }

    private void pathChanged() {
        // we do not care if the path is invalid.
        state.path = Paths.get(fileTextField.getText());
    }

    public static class FileChooserWidgetProps {
        FileChooserWidget self;
        Path initialFile;
        Supplier<Path> fileChoiceStrategy;

        public FileChooserWidget getSelf() {
            return self;
        }

        public void setSelf(FileChooserWidget self) {
            this.self = self;
        }

        public void setInitialFile(Path initialFile) {
            this.initialFile = initialFile;
        }

        public void setFileChoiceStrategy(Supplier<Path> fileChoiceStrategy) {
            this.fileChoiceStrategy = fileChoiceStrategy;
        }
    }

    public enum FileChooserMode {
        OPEN, SAVE
    }

    public static class FileChooserWidgetState {
        /**
         * The currently selected path,
         * essentially the text that is in the box,
         * whether it was pasted or typed into the box,
         * or put there by the popout file chooser window.
         */
        public Path path;
    }

    public FileChooserWidgetState getState() {
        return state;
    }
}
